	function quitarIdLista(id, lista) {
		var ids = lista.split(',');

		for (var i = 0; i <= ids.length; i++) {
			if (ids[i] === id) {
				ids.splice(i, 1);
			}
		};

		return ids.toString();
	}


	function agregarIdLista(id, lista) {
		var ids = lista.split(',');

		ids.push(id);

		return ids.toString();
	}


